      + div svg {
        color: @subtext0;
      }
    }

    [data-a-target="video-ad-label"],
    [data-a-target="video-ad-countdown"] {
      color: @text !important;
    }

    /* Verified badge checkmark */
    path[fill="#fff"] {
      fill: @crust !important;
    }

    /* Override for logo icon */
    .tw-animated-glitch-logo {
      --color-white: @crust;
    }

    /* Modals */
    .modal__content {
      color: @text;
    }

