    figure[typeof~="mw:File/Thumb"] {
      background-color: @mantle !important;
      color: @text !important;
      border-top-color: @surface2;
      border-left-color: @surface2;
      border-right-color: @surface2;
      > figcaption {
        background-color: @mantle !important;
        color: @text !important;
        border-bottom-color: @surface2;
        border-left-color: @surface2;
        border-right-color: @surface2;
      }
      > :not(figcaption) .mw-file-element {
        color: @surface2 !important;
        border-color: @surface2;
      }
    }

    .mw-parser-output #mp-left,
    .mw-parser-output #mp-left .mp-h2,
    th[class="sidebar-title"] {
      background-color: fade(@green, 10%) !important;
      border-color: fade(@green, 20%) !important;
    }

    .mw-plusminus-pos {
      color: @green !important;
    }

    .mw-parser-output #mp-right,
    .mw-parser-output #mp-right .mp-h2 {
      background-color: fade(@blue, 10%) !important;
      border-color: fade(@blue, 20%) !important;
    }

    .mw-parser-output #mp-lower,
    .mw-parser-output #mp-lower .mp-h2 {
      background-color: fade(@mauve, 10%) !important;
      border-color: fade(@mauve, 20%) !important;
    }

    .mw-collapsible-toggle-default:active .mw-collapsible-text {
      color: @peach;
    }

    .mw-parser-output #mp-middle,
    .mw-parser-output #mp-middle .mp-h2 {
      background-color: fade(@pink, 10%) !important;
      border-color: fade(@pink, 20%) !important;
    }

    .mw-parser-output .navbox-title,
    .summary,
    .infobox > tbody:nth-child(1) > tr:nth-child(4) > th:nth-child(1),
    .infobox > tbody:nth-child(1) > tr:nth-child(6) > th:nth-child(1) {
      background-color: fade(@accent-color, 20%) !important;
    }

    .infobox > tbody:nth-child(1) > tr:nth-child(2) > td:nth-child(1) {
      background-color: @surface1 !important;
    }

    .mw-parser-output .navbox-group,
    table.expanded:nth-child(2) > tbody:nth-child(1) > tr:nth-child(3),
    table.expanded:nth-child(2)
      > tbody:nth-child(1)
      > tr:nth-child(4)
      > td:nth-child(2)
      > table:nth-child(2)
      > tbody:nth-child(1)
      > tr:nth-child(1),
    table.expanded:nth-child(2) > tbody:nth-child(1) > tr:nth-child(1),
    table.nowraplinks:nth-child(1)
      > tbody:nth-child(1)
      > tr:nth-child(3)
      > td:nth-child(1)
      > table:nth-child(2)
      > tbody:nth-child(1)
      > tr:nth-child(1),
    table.nowraplinks:nth-child(1)
      > tbody:nth-child(1)
      > tr:nth-child(4)
      > td:nth-child(1)
      > table:nth-child(2)
      > tbody:nth-child(1)
      > tr:nth-child(1),
    table.nowraplinks:nth-child(1)
      > tbody:nth-child(1)
      > tr:nth-child(5)
      > td:nth-child(1)
      > table:nth-child(2)
      > tbody:nth-child(1)
      > tr:nth-child(1),
    .navbox-abovebelow {
      background-color: @surface1 !important;
    }

    .mw-parser-output .tracklist > tbody {
      color: inherit;

      > .tracklist-total-length * {
        background-color: @overlay1;
        color: @base;
      }
    }

    .mw-content-ltr
      > table:nth-child(20)
      > tbody:nth-child(1)
      > tr:nth-child(2)
      > td:nth-child(2) {
      border-color: @surface2 !important;
      background-color: @surface0 !important;
    }

    .noarticletext,
    #noarticletext {
      background-color: @base !important;
    }

    #sisterproject {
      background-color: @mantle !important;
    }

    [style="color:#02a64f;line-height:initial"] {
      color: @green !important;
    }

    [style="color:#f78e1e;line-height:initial"] {
      color: @peach !important;
    }

    [style="color:#77278b;line-height:initial"] {
      color: @mauve !important;
    }

    [style="color:#87746a;line-height:initial"] {
      color: @maroon !important;
    }

    [style="color:#009aC8;line-height:initial"] {
      color: @sky !important;
    }

    [style="color:#ffd520;line-height:initial"] {
      color: @yellow !important;
    }

    [style="color:#0060a9;line-height:initial"] {
      color: @blue !important;
    }

    table.nowraplinks:nth-child(4) > tbody:nth-child(1) > tr:nth-child(1),
    .navbox-list-with-group.navbox-list.navbox-odd {
      background-color: @surface0 !important;
    }

    .mw-parser-output tr + tr > .navbox-abovebelow {
      border-color: @base;
    }

    .catlinks li {
      border-left-color: @surface2;
    }

    /* Fundraising banner */
    .frb-btn,
    .frb-label {
      color: @text;
      background-color: @base;
      border-color: @overlay0;
    }

    input[type="radio"]:checked + .frb-btn,
    input[type="radio"]:checked + .frb-label,
    .frb-btn:hover,
    .frb-label:hover {
      color: @base;
      background-color: @accent-color;
      border-color: @accent-color;
    }

    #frb-main {
      --wmui-base100: @base;
      --wmui-base0: @text;
      --frb-body: @text;
      --frb-muted: @subtext0;
      --frb-link: @subtext0;
      --frb-link-hover: @text;
      --frb-error: @red;
      --frb-submit: @accent-color;
      --frb-submit-hover: darken(@accent-color, 10%);

      svg.frb-message-icon > g {
        circle {
          fill: @yellow;
        }
        path {
          fill: @base;
        }
      }

      .frb-message {
        background-color: @green;
        border-color: @green;
      }

      .frb-message::after {
        border-left-color: @green;
      }
    }

    #frb-inline {
      --wmui-base100: @base;
      --frb-primary: @red;
      --frb-link: @accent-color;
      --frb-muted: @subtext0;
      --frb-muted-hover: @text;
      --frb-body: @text;
      --frb-error: @red;
      --wmui-red-dark: @red;
      --wmui-red-light: @red;
      --wmui-green-dark: @green;
      --wmui-green-light: @green;
      --frb-submit: @accent-color;
      --frb-submit-hover: darken(@accent-color, 10%);

      .frb-inline-topbar {
        color: @text;

        svg circle {
          fill: @yellow;
        }

        svg path {
          fill: @base;
        }
      }

      .frb-btn-cta {
        background-color: @accent-color !important;
        border-color: @accent-color !important;
        color: @base !important;
      }

      .frb-btn-simple {
        color: @accent-color !important;
      }

      .frb-monthly-pitch {
        color: @blue;
      }

      .frb-optin-no-prompt {
        color: @base;
      }

      .frb-cta-hiddenmessage {
        background-color: @red;
        color: @base;
      }
    }

    #frb-nag {
      --frb-primary-light: @base;
      --frb-body: @text;
      --wmui-accent-dark: @subtext0;
      --frb-link-hover: @text;

      span.frb-donate-button {
        color: @base;
        background-color: @red;

        &:hover {
          color: @base;
          background-color: darken(@red, 10%);
        }
      }

      svg.frb-icon-close > g {
        stroke: @subtext0;

        &:hover {
          stroke: @text;
        }
      }
    }

    .frb-nag:not(#frb-nag) {
      --frb-message-background: @base;
      --frb-message-border: @red;
      --frb-message: @text;
      border-color: @surface0;
      background: @base;
      box-shadow: none;

      #nag-rml-btn {
        background-color: transparent;
        border-color: transparent;
        color: @subtext0;
      }

      #nag-yes-btn {
        background-color: @accent-color;
        border-color: @accent-color;
        color: @base;
      }
    }

    .frb-btn-cta-label::after {
      @svg: escape(
        '<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 6.7 12"><path d="M.9 0 0 .9 5.1 6 0 11.1l.9.9 5.5-5.5c.3-.3.3-.7 0-.9z" fill="@{base}"/></svg>'
      );
      background-image: url("data:image/svg+xml,@{svg}") !important;
    }

    .frb-back {
      @svg-raw: '<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 16"><g fill="none" fill-rule="evenodd" transform="translate(1 1)"><path stroke="@{subtext0}" stroke-linecap="round" stroke-linejoin="round" stroke-width="1.778" d="M7.181 13.285.753 7 7.181.715"/><rect fill="@{subtext0}" width="18.182" height="1.778" x=".818" y="6.111" rx=".889"/></g></svg>';
      @svg: escape(@svg-raw);
      background-image: url("data:image/svg+xml,@{svg}") !important;
    }

    .frb-close {
      @svg-raw: '<svg viewBox="0 0 10 10" xmlns="http://www.w3.org/2000/svg"><g stroke="@{subtext0}" fill="none" stroke-linecap="round"><path d="M1 1 l8 8 M9 1 l-8 8"></path></g></svg>';
      @svg: escape(@svg-raw);
      background-image: url("data:image/svg+xml,@{svg}") !important;
    }

